const STYLES = [
    'btn--primary',
    'btn--outline'
]

const SIZES = [
    'btn--medium',
    'btn--large'
]

export const Button = ({
    children,
    type,
    onClick,
    buttonStyle,
    buttonSize
}) => {

    const checkButtonStyle = STYLES.icludes(buttonStyle) ? buttonStyle : STYLES[0]

    const checkButtonSize = SIZES.icludes(buttonSize) ? buttonSize : SIZES[0]

        return (
            <button className={'btn ${checkButtonStyle} ${checkButtonSize}'} onClick={onClick} type={type}>
                {children}
            </button>
        )
}
